import numpy as np


def calcula(val):
    # srting val = "[-0.22885429,  1.22885429]"
    x = float(val[0])
    y = float(val[1])

    r = (x-0.5)**2+(y-0.5)**2
    #f = (np.cos(n*math.pi*r)**2)*np.exp(-r**2/s2)
    f = np.exp(-r)
    return f

#print(calcula([0.5203344847639677, -4.887061385092341]))

