    # srting vals = [[-0.22885429,  1.22885429],[-0.22885429,  1.22885429],[-0.22885429,  1.22885429]]
    calcs = []
    for i in range(len(vals)):
        calcs.append(calcula(vals[i]))
    print(calcs)

